package com.troll.trollbucketimport androidx.lifecycle.DefaultLifecycleObserverimport androidx.lifecycle.LifecycleOwnerimport troll.btc.log.TLog/** * author : TangPeng * date : 3/22/23 10:52 * description : */class ApplicationLifecycleOwner : DefaultLifecycleObserver {    private val TAG = "ApplicationLifecycleOwner"    override fun onStart(owner: LifecycleOwner) {        super.onStart(owner)        TLog.i(TAG, "onStart")    }    override fun onStop(owner: LifecycleOwner) {        super.onStop(owner)        TLog.i(TAG, "onStop")    }    override fun onDestroy(owner: LifecycleOwner) {        super.onDestroy(owner)        TLog.i(TAG, "onDestroy")    }    override fun onResume(owner: LifecycleOwner) {        super.onResume(owner)        TLog.i(TAG, "onResume")    }    override fun onPause(owner: LifecycleOwner) {        super.onPause(owner)        TLog.i(TAG, "onPause")    }}