package com.troll.trollbucket/** * author : TangPeng * date : 1/9/23 18:45 * description : */class ObservableArrayList<T>(private val onListAddListener: OnListAddListener<T>?) :    ArrayList<T>() {    override fun add(element: T): Boolean {        val add = super.add(element)        onListAddListener?.add(this, size - 1)        return add    }    interface OnListAddListener<T> {        fun add(list: ArrayList<T>, index: Int)    }}