package com.troll.trollbucketimport android.content.Contextimport android.graphics.*import android.util.AttributeSetimport android.widget.FrameLayoutclass ShadeView @JvmOverloads constructor(    context: Context, attrs: AttributeSet? = null, defStyleAttr: Int = 0) : FrameLayout(context, attrs, defStyleAttr) {    init {        //关闭当前view的硬件加速        //关闭当前view的硬件加速        setLayerType(LAYER_TYPE_SOFTWARE, null)        setBackgroundColor(Color.parseColor("#EF000000"))    }    private val paint by lazy {        Paint().apply {            isAntiAlias = true            xfermode = PorterDuffXfermode(PorterDuff.Mode.CLEAR)        }    }    private val dashPaint by lazy {        Paint().apply {            isAntiAlias = true            strokeWidth = 3f            style = Paint.Style.STROKE            color = Color.parseColor("#FFFFFF")            pathEffect = DashPathEffect(floatArrayOf(20f, 20f), 20f)        }    }    var vertical: Float = 0f        set(value) {            field = value            invalidate()        }        get() = field    var horizontal: Float = 0f        set(value) {            field = value            invalidate()        }        get() = field    override fun onDraw(canvas: Canvas?) {        super.onDraw(canvas)        canvas?.drawRoundRect(            100f,            100f + vertical,            700f - horizontal,            1500f - vertical,            20f,            20f,            paint        )        canvas?.drawRoundRect(            90f,            90f + vertical,            710f - horizontal,            1510f - vertical,            20f,            20f,            dashPaint        )    }}