package com.troll.trollbucketimport androidx.lifecycle.Lifecycleimport androidx.lifecycle.ProcessLifecycleOwnerimport com.troll.common.CommonApplication/** * author : TangPeng * date : 3/7/22 16:16 * description : */class TrollApplication : CommonApplication() {    private val lifecycleOb by lazy { ApplicationLifecycleOwner() }    override fun onCreate() {        super.onCreate()        GlobalGray.hook()        ProcessLifecycleOwner.get().lifecycle.addObserver(lifecycleOb)    }    companion object {        // 是否处于前台        val isForeground            get() = ProcessLifecycleOwner.get().lifecycle.currentState.isAtLeast(Lifecycle.State.STARTED)    }}