package com.troll.trollbucketimport android.app.Activityimport android.app.Applicationimport androidx.multidex.MultiDexApplicationimport com.alibaba.android.arouter.launcher.ARouterimport troll.btc.log.TLogimport troll.fitter.AdaptListenerimport troll.fitter.FitterConfigimport troll.fitter.FitterSize/** * author : TangPeng * date : 3/7/22 16:16 * description : */class TrollApplication : MultiDexApplication() {    override fun onCreate() {        super.onCreate()        FitterSize.initCompatMultiProcess(this)        ARouter.openLog()        ARouter.openDebug()        ARouter.init(this)        FitterConfig.get()            .addAdaptListener(object : AdaptListener {                override fun onAdaptBefore(target: Any, activity: Activity) {                    TLog.i("适配前${activity.javaClass.name}")                }                override fun onAdaptAfter(target: Any, activity: Activity) {                    TLog.i("适配后${activity.javaClass.name}")                }            })    }}