package com.troll.barimport android.Rimport android.app.Activityimport android.os.Buildimport android.view.ViewGroupimport android.view.Windowimport android.view.WindowManager/** * author : TangPeng * date : 8/8/22 17:00 * description : */class TrollBar {    private var window: Window? = null    private var decorView: ViewGroup? = null    private var contentView: ViewGroup? = null    fun init(activity: Activity) {        initParameter(activity.window)    }    /**     * 适配刘海屏     */    private fun fitNotchScreen() {        runCatching {            if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.P) {                try {                    window?.let {                        val lp: WindowManager.LayoutParams = it.attributes                        lp.layoutInDisplayCutoutMode =                            WindowManager.LayoutParams.LAYOUT_IN_DISPLAY_CUTOUT_MODE_SHORT_EDGES                        it.attributes = lp                    }                } catch (e: Exception) {                }            }        }    }    /**     * 初始化状态栏 和 导航栏     */    private fun setBar() {    }    private fun initParameter(window: Window) {        this.window = window        decorView = window.decorView as ViewGroup        contentView = decorView?.findViewById(R.id.content)    }}