package troll.btc.extensionsimport android.app.Activityimport android.view.Viewimport androidx.annotation.IdResimport androidx.core.app.ActivityCompatimport androidx.core.view.ViewCompatfun <V : View> View.requireViewByIdCompat(@IdRes id: Int): V {    return ViewCompat.requireViewById(this, id)}fun <V : View> Activity.requireViewByIdCompat(@IdRes id: Int): V {    return ActivityCompat.requireViewById(this, id)}