package troll.btc.extensionsimport troll.btc.constants.EMPTYimport troll.btc.log.TLogtypealias NoArgBlock = () -> Unittypealias Block<T> = (T) -> Unitinline fun <T> T.self(action: NoArgBlock): T {    action()    return this}fun <T> T.checkNull(string: String = "nullPointerException") {    if (this == null) {        throw NullPointerException(string)    }}fun  tryCatch(msg:String = EMPTY){    try {    }catch (e:Exception){        TLog.e(msg)    }}