package troll.btc.extensionsimport android.content.Contextimport android.content.Intentimport android.view.Displayimport android.view.LayoutInflaterimport android.view.WindowManagerfun Context.startAc(cls: Class<*>) {    this.startActivity(Intent(this, cls))}fun Context.layoutInflater(): LayoutInflater {    return LayoutInflater.from(this)}fun Context.getDefaultDisplay(): Display? {//    return if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.R) {//        this.display//    } else {    val wm = this.getSystemService(Context.WINDOW_SERVICE) as WindowManager    return wm.defaultDisplay//    }}