package troll.btc.extensionsimport kotlinx.coroutines.CoroutineScopeimport kotlinx.coroutines.CoroutineStartimport kotlinx.coroutines.Jobimport kotlinx.coroutines.flow.Flowimport kotlinx.coroutines.launchimport kotlin.coroutines.CoroutineContextimport kotlin.coroutines.EmptyCoroutineContext/** * author : TangPeng * date : 5/11/22 23:47 * description : */fun <T> Flow<T>.launch(    scope: CoroutineScope,    context: CoroutineContext = EmptyCoroutineContext,    start: CoroutineStart = CoroutineStart.DEFAULT,    result: (T) -> Unit): Job {    return scope.launch(context, start) {        collect {            result(it)        }    }}