package troll.btc.extensionsimport java.math.RoundingModeimport java.text.DecimalFormatimport java.text.NumberFormat/** * author : TangPeng * date : 5/15/22 10:22 * description : */private val decimalFormat by lazy(LazyThreadSafetyMode.SYNCHRONIZED) {    NumberFormat.getInstance() as DecimalFormat}fun Double.format(    fractionDigits: Int,    mode: RoundingMode = RoundingMode.HALF_UP,    groupUsed: Boolean = false): String {    decimalFormat.apply {        isGroupingUsed = groupUsed        minimumFractionDigits = fractionDigits        maximumFractionDigits = fractionDigits        roundingMode = mode    }    return decimalFormat.format(this)}fun Float.format(    fractionDigits: Int,    mode: RoundingMode = RoundingMode.HALF_UP,    groupUsed: Boolean = false): String {    decimalFormat.apply {        isGroupingUsed = groupUsed        minimumFractionDigits = fractionDigits        maximumFractionDigits = fractionDigits        roundingMode = mode    }    return decimalFormat.format(this)}fun String?.toLong(): Long {    return try {        if (isNullOrEmpty()) {            0L        } else {            java.lang.Long.parseLong(this ?: return 0L)        }    } catch (e: Exception) {        0L    }}fun String?.toDouble(): Double {    return try {        if (isNullOrEmpty()) {            0.0        } else {            java.lang.Double.parseDouble(this ?: return 0.0)        }    } catch (e: Exception) {        0.0    }}fun String?.toFloat(): Float {    return try {        if (isNullOrEmpty()) {            0.0f        } else {            java.lang.Float.parseFloat(this ?: return 0f)        }    } catch (e: Exception) {        0.0f    }}fun String?.toInt(): Int {    return try {        if (isNullOrEmpty()) {            0        } else {            Integer.parseInt(this ?: return 0)        }    } catch (e: Exception) {        0    }}fun Int?.orZero(): Int {    return this ?: 0}fun Float?.orZero(): Float {    return this ?: 0f}fun Long?.orZero(): Long {    return this ?: 0}