package troll.btc.extensionsimport android.text.TextUtilsimport java.net.URIfun String?.appendUriPara(append: String): String {    fun processUri(): String {        if (TextUtils.isEmpty(this)) throw Exception("The url is  empty.")        val oldUri = URI(this)        var query = oldUri.query        query = if (query == null) {            append        } else {            "$query&$append"        }        val newUri = URI(oldUri.scheme, oldUri.authority, oldUri.path, query, oldUri.fragment)        return newUri.toString()    }    runCatching {        processUri()    }.onSuccess {        return it    }.onFailure {        return it.message ?: "The url is  error"    }    return ""}