package troll.btc.logimport android.util.Log/** * author : TangPeng * date : 3/11/22 15:06 * description : */object TLog {    private const val TAG = "T_Log"    var debug = true    fun d(message: String, tag: String = TAG) {        if (debug) Log.d(tag, message)    }    fun i(message: String, tag: String = TAG) {        if (debug) Log.i(tag, message)    }    fun e(message: String, tag: String = TAG) {        if (debug) Log.e(tag, message)    }}