package troll.btc.utilimport android.content.Contextimport android.util.DisplayMetricsimport troll.btc.extensions.getDefaultDisplay/** * author : TangPeng * date : 3/11/22 15:19 * description : 屏幕工具类 */object ScreenUtils {    /**     * 获取屏幕大小     */    fun getScreenSize(context: Context): IntArray {        val defaultDisplay = context.getDefaultDisplay()        val displayMetrics = DisplayMetrics()        defaultDisplay?.getMetrics(displayMetrics)        return IntArray(2).apply {            this[0] = displayMetrics.widthPixels            this[1] = displayMetrics.heightPixels        }    }}