package com.troll.busimport com.troll.bus.flow.FlowBusimport troll.btc.extensions.throwIfNull/** * author : TangPeng * date : 5/12/22 00:11 * description : */class Bus private constructor() : FlowBus() {    companion object {        private var bus: Bus? = null            get() {                if (field == null) field = Bus()                return field            }        fun get(): Bus {            return bus.throwIfNull("bus is null")        }        fun close() {            bus = null        }    }}