package troll.eth.base.rvimport androidx.recyclerview.widget.RecyclerViewimport androidx.viewbinding.ViewBindingimport troll.btc.extensions.onClick/** * author : TangPeng * date : 3/4/22 16:28 * description : */abstract class BaseViewHolder<T>(bd: ViewBinding, private val click: (T) -> Unit) :    RecyclerView.ViewHolder(bd.root) {    private var data: T? = null    init {        bd.root.onClick {            data?.let { click.invoke(it) }        }    }    open fun bind(data: T) {        this.data = data    }}