package troll.eth.base.viewbindingimport android.os.Bundleimport android.view.LayoutInflaterimport androidx.appcompat.app.AppCompatActivityimport androidx.viewbinding.ViewBinding/** * ViewBinding 的形式 */abstract class ActivityEx<VB : ViewBinding> : AppCompatActivity() {    lateinit var binding: VB        private set    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding = getBinding(layoutInflater)        setContentView(binding.root)        flowData()        flowView()    }    abstract fun getBinding(inflater: LayoutInflater): VB    abstract fun flowData()    abstract fun flowView()}