package troll.eth.base.viewbindingimport android.os.Bundleimport android.view.LayoutInflaterimport androidx.appcompat.app.AppCompatActivityimport androidx.viewbinding.ViewBinding/** * ViewBinding 的形式 */abstract class BaseActivity<BD : ViewBinding> : AppCompatActivity() {    lateinit var bd: BD        private set    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        bd = getBinding(layoutInflater)        setContentView(bd.root)        flowData()        flowView()    }    abstract fun getBinding(inflater: LayoutInflater): BD    abstract fun flowData()    abstract fun flowView()}