package troll.eth.base.viewbindingimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.Fragmentimport androidx.viewbinding.ViewBinding/** * author : TangPeng * date : 2/28/22 17:25 * description : */abstract class BaseFragment<BD : ViewBinding> : Fragment() {    lateinit var bd: BD        private set    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        bd = getBinding(layoutInflater)        return bd.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        flowData()        flowView()    }    abstract fun getBinding(inflater: LayoutInflater): BD    abstract fun flowData()    abstract fun flowView()}