package troll.eth.base.viewbindingimport androidx.lifecycle.ViewModelimport troll.eth.GenericUtilimport kotlin.reflect.KClassabstract class ViewModelEx : ViewModel() {    open fun onCreate() {}    final override fun onCleared() {        onDestroy()    }    open fun onDestroy() {}}fun <VM : ViewModelEx> getViewModelClass(any: Any) = getViewModelKClass<VM>(any).java@Suppress("UNCHECKED_CAST")fun <VM : ViewModelEx> getViewModelKClass(any: Any) =    requireNotNull(GenericUtil.getClassType(any, ViewModel::class) as KClass<VM>)