package troll.fitterimport android.os.Parcelimport android.os.Parcelable/** * author : TangPeng * date : 3/11/22 18:00 * description : */class ExternalAdaptInfo() : Parcelable {    var baseOnWidth: Boolean = false    var sizeInDp: Float = 0f    constructor(parcel: Parcel) : this() {        baseOnWidth = parcel.readByte() != 0.toByte()        sizeInDp = parcel.readFloat()    }    override fun writeToParcel(parcel: Parcel, flags: Int) {        parcel.writeByte(if (baseOnWidth) 1 else 0)        parcel.writeFloat(sizeInDp)    }    override fun describeContents(): Int {        return 0    }    companion object CREATOR : Parcelable.Creator<ExternalAdaptInfo> {        override fun createFromParcel(parcel: Parcel): ExternalAdaptInfo {            return ExternalAdaptInfo(parcel)        }        override fun newArray(size: Int): Array<ExternalAdaptInfo?> {            return arrayOfNulls(size)        }    }}