package troll.fitter/** * author : TangPeng * date : 3/11/22 17:57 * description : */class ExternalAdaptManager {    private var cancelAdaptList: MutableList<String>? = null    private var externalAdaptInfo: MutableMap<String, ExternalAdaptInfo>? = null     var run = false    @Synchronized    fun addCancelAdaptOfActivity(targetClass: Class<*>): ExternalAdaptManager {        if (run.not()) run = true        if (cancelAdaptList == null) {            cancelAdaptList = mutableListOf()        }        cancelAdaptList?.add(targetClass.canonicalName)        return this    }    @Synchronized    fun addExternalAdaptInfoOfActivity(): ExternalAdaptManager {        if (run.not()) run = true        if (externalAdaptInfo == null) {            externalAdaptInfo = HashMap(16)        }        return this    }    @Synchronized    fun cancelAdapt(targetClass: Class<*>): Boolean {        if (cancelAdaptList == null) return false        return cancelAdaptList?.contains(targetClass.canonicalName) == true    }}