package troll.fitterimport android.app.Application/** * author : TangPeng * date : 3/11/22 11:25 * description : */object FitterUtil {    fun getApplicationByReflect(): Application {        val activityThread = Class.forName("android.app.ActivityThread")        val thread = activityThread.getMethod("currentActivityThread").invoke(null)        val app = activityThread.getMethod("getApplication")            .invoke(thread) ?: throw NullPointerException(" Fitter should init!!!!!")        return app as Application    }}