package troll.fitter.strategyimport android.app.Activityimport troll.btc.log.TLogimport troll.fitter.FitterConfigimport troll.fitter.adapt.CancelAdapt/** * author : TangPeng * date : 3/11/22 17:21 * description : */class DefaultAdaptStrategy : AdaptStrategy {    override fun applyAdapt(target: Any, activity: Activity) {        // 开启检查外部第三方库的适配模式，这个需要主动调用才会有的        if (FitterConfig.get().externalAdaptManager.run) {            // 适配第三方的适配            if (FitterConfig.get().externalAdaptManager.cancelAdapt(target.javaClass)) {            }        }        // 取消那些有实现cancelAdapt 接口的，这些都是放弃适配        if (target is CancelAdapt) {            TLog.i("","取消适配的Activity ${target::class.java.name}")            // todo 取消适配        }    }}