package troll.fitter.strategyimport android.app.Activityimport troll.fitter.FitterConfig/** * author : TangPeng * date : 3/11/22 17:44 * description : */class WrapperAdaptStrategy(val strategy: AdaptStrategy?) : AdaptStrategy {    override fun applyAdapt(target: Any, activity: Activity) {        FitterConfig.get().adaptListener?.let {            it.onAdaptBefore(target, activity)            strategy?.let {                strategy.applyAdapt(target, activity)            }            it.onAdaptAfter(target, activity)        }    }}