package com.troll.netimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport java.util.concurrent.TimeUnitobject RetrofitManager {    var api: INetApi? = null    var API_URL = "https://www.wanandroid.com"    fun getApiService(): INetApi {        if (api == null) {            synchronized(this) {                if (api == null) {                    val client = buildOkhttpClient()                    api = buildRetrofit(API_URL, client).create(INetApi::class.java)                }            }        }        return api!!    }    private const val CONNECTION_TIME_OUT = 15L    private const val READ_TIME_OUT = 15L    private fun buildOkhttpClient(): OkHttpClient.Builder {        return OkHttpClient.Builder()            .connectTimeout(CONNECTION_TIME_OUT, TimeUnit.SECONDS)            .readTimeout(READ_TIME_OUT, TimeUnit.SECONDS)    }    private fun buildRetrofit(baseUrl: String, builder: OkHttpClient.Builder): Retrofit {        return Retrofit.Builder()            .baseUrl(baseUrl)            .addConverterFactory(GsonConverterFactory.create())            .client(builder.build())            .build()    }}