package troll.eth.baseimport android.os.Bundleimport android.view.LayoutInflaterimport androidx.appcompat.app.AppCompatActivityimport androidx.viewbinding.ViewBindingabstract class ActivityEx<VB : ViewBinding> : AppCompatActivity() {    lateinit var binding: VB        private set    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding = getBinding(layoutInflater)        setContentView(binding.root)        flowData()    }    abstract fun flowData()    abstract fun getBinding(inflater: LayoutInflater): VB}