package com.troll.algorithm.leetCode.n110;import com.troll.algorithm.二叉树.TreeNode;/** * author : TangPeng * date : 11/13/22 20:04 * description : */public class 平衡二叉树 {    public boolean isBalanced(TreeNode root) {        return getHeight(root) != -1;    }    public int getHeight(TreeNode root) {        if (root == null) return 0;        int leftHeight = getHeight(root.left);        if (leftHeight == -1) return -1;        int rightHeight = getHeight(root.right);        if (rightHeight == -1) return -1;        if (Math.abs(leftHeight - rightHeight) > 1) {            return -1;        }        return Math.max(leftHeight, rightHeight) + 1;    }}