package com.troll.algorithm.leetCode.n242;import java.util.HashMap;/** * author : TangPeng * date : 11/1/22 01:02 * description : */public class 有效数字的异位词 {    public static void main(String[] args) {        isAnagram("a", "ab");    }    public static boolean isAnagram(String s, String t) {        int[] record = new int[26];        if (s.length() < t.length()) {            String temp = s;            s = t;            t = temp;        }        for (int i = 0; i < s.length(); i++) {            record[s.charAt(i) - 'a'] ++;        }        for (int i = 0; i < t.length(); i++) {            record[t.charAt(i) - 'a'] --;        }        for (int i : record) {            if (i !=0) {                return false;            }        }        return true;    }}