package com.troll.algorithm.leetCode.n343;/** * author : TangPeng * date : 12/5/22 23:18 * description : */public class 整数拆分 {    public int integerBreak(int n) {        int[] dp = new int[n + 1];        dp[2] = 1;        for (int i = 4; i <= n; i++) {            for (int j = 1; j <= i / 2; j++) {                dp[i] = Math.max(dp[i], Math.max(j * (i - j), j * dp[i - j]));            }        }        return dp[n];    }}