package com.troll.algorithm.leetCode.n55;/** * author : TangPeng * date : 11/26/22 21:13 * description : */public class 跳跃游戏 {    public boolean canJump(int[] nums) {        if (nums.length == 1) {            return true;        }        int coverRange = 0;        for (int i = 0; i <= coverRange; i++) {            coverRange = Math.max(coverRange, i + nums[i]);            if (coverRange >= nums.length - 1) {                return true;            }        }        return false;    }}