package com.troll.algorithm.leetCode.n599;import java.util.ArrayList;import java.util.HashMap;import java.util.List;/** * author : TangPeng * date : 3/14/22 09:30 * description : */public class 两个列表的最小索引总和 {    String[] findRestaurant(String[] list1, String[] list2) {        if (null == list1 || 0 == list1.length || list2 == null || list2.length == 0)            return new String[0];        HashMap<String, Integer> map = new HashMap<>();        for (int i = 0; i < list1.length; i++) {            map.put(list1[i], i);        }        int max = Integer.MAX_VALUE;        List<String> list = new ArrayList<>();        for (int j = 0; j < list2.length; j++) {            if (map.containsKey(list2[j])) {                int cur = j + map.get(list2[j]);                if (cur == max) {                    list.add(list2[j]);                } else if (cur < max) {                    max = cur;                    list.clear();                    list.add(list2[j]);                }            }        }        return list.toArray(new String[0]);    }}