package com.troll.algorithm.链表;/** * author : TangPeng * date : 5/20/22 08:37 * description : */public class ListNode {    public int val;    public ListNode next;    public ListNode() {    }    public ListNode(int val) {        this.val = val;    }    public ListNode(int val, ListNode next) {        this.val = val;        this.next = next;    }}