package com.troll.algorithm.链表.n203;import com.troll.algorithm.链表.ListNode;/** * author : TangPeng * date : 5/20/22 08:36 * description : */public class 移除链表元素 {    public ListNode removeElements(ListNode head, int val) {        while (head != null && head.val == val) {            head = head.next;        }        if (head == null) return null;        ListNode pre = head;        ListNode cur = head.next;        while (cur != null) {            if (cur.val == val) {                pre.next = cur.next;            } else {                pre = cur;            }            cur = cur.next;        }        return head;    }}