package com.troll.algorithm.链表.n203;import com.troll.algorithm.链表.ListNode;/** * author : TangPeng * date : 5/20/22 08:36 * description : */public class 移除链表元素 {    public ListNode removeElements(ListNode head, int val) {        while (head != null && head.getVal() == val) {            head = head.getNext();        }        if (head == null) return null;        ListNode pre = head;        ListNode cur = head.getNext();        while (cur != null) {            if (cur.getVal() == val) {                pre.setNext(cur.getNext());            } else {                pre = cur;            }            cur = cur.getNext();        }        return head;    }}