package com.troll.algorithm.链表.n206;import com.troll.algorithm.链表.ListNode;/** * author : TangPeng * date : 8/1/22 07:44 * description : * 给你单链表的头节点 head ，请你反转链表，并返回反转后的链表。 */public class 反转链表 {    public ListNode reverseList(ListNode head) {        ListNode pre = null;        ListNode cur = head;        ListNode temp;        while (cur != null) {            temp = head.next;            cur.next = pre;            pre = cur;            cur = temp;        }        return pre;    }}