package troll.asmimport org.objectweb.asm.ClassVisitorimport com.android.build.api.instrumentation.*/** * author : TangPeng * date : 6/6/22 20:36 * description : */abstract class TimeCostTransform: AsmClassVisitorFactory<InstrumentationParameters.None> {    override fun createClassVisitor(classContext: ClassContext, nextClassVisitor: ClassVisitor): ClassVisitor {        return TimeCostClassVisitor(nextClassVisitor)    }    override fun isInstrumentable(classData: ClassData): Boolean {        return true    }}