package troll.composeimport android.os.Bundleimport androidx.activity.compose.setContentimport androidx.appcompat.app.AppCompatActivityimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.material.*import androidx.compose.runtime.*import androidx.compose.ui.Modifierimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport troll.compose.theme.TestComposeTheme/** * author : TangPeng * date : 3/30/22 09:54 * description : */class ComposeActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContent {            TestComposeTheme {                Surface(                    modifier = Modifier.fillMaxSize(),                    color = MaterialTheme.colors.background                ) {//                    Greeting("Android")                    HelloContent()                }            }        }    }}@Composablefun Greeting(name: String) {    Text(text = "Hello $name!")}@Composablefun Click(onClick: () -> Unit) {    Button(onClick = onClick) {        Text("点击")    }}@Preview(showBackground = true)@Composablefun DefaultPreview() {    TestComposeTheme {        Greeting("Android")    }}@Composablefun HelloContent() {    Column(modifier = Modifier.padding(16.dp)) {        var name by remember { mutableStateOf("") }        if (name.isNotEmpty()) {            Text(                text = "Hello，$name",                modifier = Modifier.padding(bottom = 10.dp),                style = MaterialTheme.typography.h5            )        }        OutlinedTextField(            value = "",            onValueChange = {                name = it            },            label = { Text("Name") }        )    }}