package troll.kotlin.activityResultContractsimport android.app.Activityimport android.content.Intentimport android.view.LayoutInflaterimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityResultContractTwoBindingclass ResultContractsTwoActivity : BaseActivity<ActivityResultContractTwoBinding>() {    override fun getBinding(inflater: LayoutInflater): ActivityResultContractTwoBinding =        ActivityResultContractTwoBinding.inflate(inflater)    override fun flowData() {        val receiver = intent.getStringExtra("name")        bd.tv.text = receiver    }    override fun flowView() {        bd.tv.onClick {            setResult(Activity.RESULT_OK, Intent().apply {                putExtra("result", "Hello 这是第二个界面返回的数据")            })            finish()        }    }}