package troll.kotlin.activityResultContractsimport android.view.LayoutInflaterimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.eth.ext.finishWithResultimport troll.kotlin.databinding.ActivityResultContractTwoBindingclass ResultTwoActivity : BaseActivity<ActivityResultContractTwoBinding>() {    override fun getBinding(inflater: LayoutInflater): ActivityResultContractTwoBinding =        ActivityResultContractTwoBinding.inflate(inflater)    override fun flowData() {        val receiver = intent.getStringExtra("name")        bd.tv.text = receiver    }    override fun flowView() {        bd.tv.onClick {            finishWithResult("key" to "key")        }    }}