package troll.kotlin.animationimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport androidx.recyclerview.widget.DividerItemDecorationimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityAnimationListBinding/** * author : TangPeng * date : 4/24/22 11:29 * description : */class AnimationListActivity : BaseActivity<ActivityAnimationListBinding>() {    private val vm: AnimationViewModel by viewModels()    private var adapter: AnimationListAdapter? = null    override fun getBinding(inflater: LayoutInflater): ActivityAnimationListBinding =        ActivityAnimationListBinding.inflate(inflater)    override fun flowData() {        vm.data()        vm.live.observe(this) {            adapter?.submitList(it)        }    }    override fun flowView() {        adapter = AnimationListAdapter {        }        bd.animationRv.addItemDecoration(            DividerItemDecoration(                this,                DividerItemDecoration.VERTICAL            )        )        bd.animationRv.adapter = adapter    }}