package troll.kotlin.animationimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.ListAdapterimport androidx.recyclerview.widget.RecyclerViewimport troll.btc.extensions.layoutInflaterimport troll.btc.extensions.onClickimport troll.eth.base.rv.BaseDiffUtilimport troll.kotlin.databinding.LayoutItemAnimationListBinding/** * author : TangPeng * date : 4/24/22 11:39 * description :  动画测试Adapter */class AnimationListAdapter(private val click: (AnimationBean) -> Unit) :    ListAdapter<AnimationBean, AnimationListAdapter.AnimationVH>(AnimationListDiff) {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): AnimationVH {        val bd =            LayoutItemAnimationListBinding.inflate(parent.context.layoutInflater(), parent, false)        return AnimationVH(bd, click)    }    override fun onBindViewHolder(holder: AnimationVH, position: Int) {        holder.bind(getItem(position))    }    class AnimationVH(bd: LayoutItemAnimationListBinding, private val onClick: (AnimationBean) -> Unit) :        RecyclerView.ViewHolder(bd.root) {        private val btn: TextView = bd.itemButton        fun bind(data: AnimationBean) {            btn.text = data.title            btn.onClick {                onClick.invoke(data)            }        }    }}object AnimationListDiff : BaseDiffUtil<AnimationBean>() {    override fun areContentsTheSame(oldItem: AnimationBean, newItem: AnimationBean): Boolean {        return oldItem.type == newItem.type    }}