package troll.kotlin.constraintlayoutimport android.content.Contextimport android.view.LayoutInflaterimport android.view.WindowManagerimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityConstraintlayoutBinding/** * author : TangPeng * date : 3/9/22 09:54 * description : * * 角度定位 * app:layout_constraintCircle： B相对于A角度定位 * app:layout_constraintCircleAngle：角度定位角度 * app:layout_constraintCircleRadius：B中心点相对于A中心点 * * * 辅助工具 * Guideline * android:orientation：辅助线的方向 * app:layout_constraintGuide_percent：在父布局的百分比位置 * app:layout_constraintGuide_begin：距离父布局开始位置，横向为左，纵向为顶部 * app:layout_constraintGuide_end：距离父布局末尾位置 * * Barrier * app:barrierDirection：屏障，在哪个方向的屏障 * app:constraint_referenced_ids：屏障引用的id * app:barrierMargin：barrier与哪个方向的距离 * * Group * 被布局group嵌套的控件，可以同步控制隐藏、显示 * * Placeholder * * * Layer * 把一些控件组合在一起，当作一个图层，改图层自动计算边界，也是View子类 * * Flow *  flow_wrapMode：aligned: 绝对对齐，网格对齐方式   none：默认方式，排成一排 chain: * * */class ConstraintLayoutActivity : BaseActivity<ActivityConstraintlayoutBinding>() {    override fun getBinding(inflater: LayoutInflater): ActivityConstraintlayoutBinding =        ActivityConstraintlayoutBinding.inflate(inflater)    override fun flowData() {    }    override fun flowView() {    }}