package troll.kotlin.coroutinesimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityCoroutinesBindingclass CoroutinesActivity : BaseActivity<ActivityCoroutinesBinding>() {    private val vm: CoroutinesViewModel by viewModels()    private val flowVm: FlowViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityCoroutinesBinding =        ActivityCoroutinesBinding.inflate(inflater)    override fun flowData() {    }    override fun flowView() {        bd.coroutinesSuspend.onClick {            vm.testSuspendFun()        }        bd.coroutinesJob.onClick {            vm.testJob()        }        bd.coroutinesDeferred.onClick {            vm.testDeferred()        }    }}