package troll.kotlin.coroutinesimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport androidx.lifecycle.lifecycleScopeimport kotlinx.coroutines.GlobalScopeimport kotlinx.coroutines.MainScopeimport kotlinx.coroutines.launchimport kotlinx.coroutines.runBlockingimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityCoroutinesBindingclass CoroutinesActivity : BaseActivity<ActivityCoroutinesBinding>() {    private val vm: CoroutinesViewModel by viewModels()    private val flowVm: FlowViewModel by viewModels()    private val channelVm: ChannelViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityCoroutinesBinding =        ActivityCoroutinesBinding.inflate(inflater)    override fun flowData() {    }    override fun flowView() {        bd.coroutinesChannel.onClick {            channelVm.actorSend(lifecycleScope)        }        bd.coroutinesJob.onClick {            runBlocking {                val runTaskCancellableCoroutine = runTaskCancellableCoroutine()                println("协程测试代码$runTaskCancellableCoroutine")            }        }    }}