package troll.kotlin.coroutinesimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport androidx.lifecycle.lifecycleScopeimport kotlinx.coroutines.launchimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityCoroutinesBindingclass CoroutinesActivity : BaseActivity<ActivityCoroutinesBinding>() {    private val coroutinesVm: CoroutinesViewModel by viewModels()    private val flowVm: FlowViewModel by viewModels()    private val channelVm: ChannelViewModel by viewModels()    private val selectVm: SelectViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityCoroutinesBinding =        ActivityCoroutinesBinding.inflate(inflater)    override fun flowData() {    }    override fun flowView() {        bd.coroutinesOne.onClick {            lifecycleScope.launch {                with(coroutinesVm) {                    val userInfo = getUserInfo()                    val friend = getFriendList(userInfo)                    println(" ${getFeedList(friend)}")                }            }        }        bd.coroutinesJob.onClick {            coroutinesVm.job()        }        bd.coroutinesSelect.onClick {            selectVm.selectChannel(lifecycleScope)        }    }}