package troll.kotlin.coroutinesimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport kotlinx.coroutines.GlobalScopeimport kotlinx.coroutines.launchimport kotlinx.coroutines.runBlockingimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityCoroutinesBindingclass CoroutinesActivity : BaseActivity<ActivityCoroutinesBinding>() {    private val vm: CoroutinesViewModel by viewModels()    private val flowVm: FlowViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityCoroutinesBinding =        ActivityCoroutinesBinding.inflate(inflater)    override fun flowData() {    }    override fun flowView() {        bd.coroutinesSuspend.onClick {            GlobalScope.launch {                val id = vm.getId()                val avatar = vm.getAvatar(id)                println("协程测试代码$avatar")            }        }        bd.coroutinesJob.onClick {//            vm.testJob()            runBlocking {                val runTaskCancellableCoroutine = runTaskCancellableCoroutine()                println("协程测试代码$runTaskCancellableCoroutine")            }        }    }}