package troll.kotlin.coroutinesimport android.app.Activityimport android.content.Intentimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport kotlinx.coroutines.MainScopeimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityCoroutinesBindingclass CoroutinesActivity : BaseActivity<ActivityCoroutinesBinding>() {    private val vm: CoroutinesViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityCoroutinesBinding =        ActivityCoroutinesBinding.inflate(inflater)    override fun flowData() {        val receiver = intent.getStringExtra("name")        bd.coroutinesTv.text = receiver        vm.liveTest.observe(this) {//            bd.coroutinesTv.text = it        }        bd.coroutinesTv.setOnClickListener {            setResult(Activity.RESULT_OK, Intent().apply {                putExtra("result", "Hello 这是第二个界面返回的数据")            })            finish()        }    }    override fun flowView() {        MainScope()    }}