package troll.kotlin.coroutinesimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.GlobalScopeimport kotlinx.coroutines.asyncimport kotlinx.coroutines.delayimport troll.eth.base.viewbinding.BaseViewModelclass CoroutinesViewModel : BaseViewModel() {    suspend fun getId(): String {        return GlobalScope.async(Dispatchers.IO) {            delay(1000)            "Id"        }.await()    }    suspend fun getAvatar(id: String): String {        return GlobalScope.async(Dispatchers.IO)  {            delay(1000)            "头像$id"        }.await()    }}