package troll.kotlin.coroutinesimport kotlin.concurrent.threadimport kotlin.coroutines.resumeimport kotlin.coroutines.suspendCoroutine/** * author : TangPeng * date : 11/16/22 21:46 * description : */fun interface SingleMethodCallback {    fun onCallback(value: String)}fun runTask(callback: SingleMethodCallback) {    thread {        Thread.sleep(100)        callback.onCallback("接口转换成挂起函数")    }}suspend fun runTaskSuspend() = suspendCoroutine<String> { continuation ->    runTask {        continuation.resume(it)    }}